<?php

$stars = new Stars();

echo "Part1: " . $stars->part1() . PHP_EOL;
echo "Part2: " . $stars->part2() . PHP_EOL;

class Stars
{
    public function part1(): int
    {
        $level = 0;
        $input = $this->getInput();

        for ($i = 0; $i < strlen($input); ++$i) {
            '(' === $input[$i] ? ++$level : --$level;
        }

        return $level;
    }

    public function part2()
    {
        $level = 0;
        $input = $this->getInput();

        for ($i = 0; $i < strlen($input); ++$i) {
            '(' === $input[$i] ? ++$level : --$level;

            if ($level < 0) {
                return $i + 1;
            }
        }

        return 'Not found';
    }

